// Filename - component/home.js

import React, { useState } from "react";
import './styles.css';

function Login() {

    const [hide, SetHide] = useState(true);
    const [name, setName] = useState(null);
    const [email, setEmail] = useState(null);
    const [password, setPassword] = useState('');

    function handlerCount() {
        SetHide(!hide)
    }
    const handlerLogin = (email, password) => {
        this.preventDefault();
        console.log("message ennvoyer email :" + email + " password: " + password);

    }
    return <>
        <main>
            <div class="circle"></div>
            <div class="register-form-container">
                <form action="" style={{ display: hide ? 'inline' : 'none' }} className="form">
                    <h1 class="form-title">
                        AUTHENTIFICATION
                    </h1>
                    <div class="form-fields">
                        {/* <div class="form-field">
            <input type="text" placeholder="Name" required pattern="[a-zA-Z]+" title="Name can only contain letters."/>
          </div> */}
                        <div class="form-field">
                            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" required />
                        </div>
                        <div class="form-field">
                            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" required minlength="8" maxlength="128" />
                        </div>
                    </div>
                    <div class="form-buttons">
                        <button class="button"
                            onClick={() => { handlerLogin() }}
                        >CONNECTION</button>
                        <div class="divider">ou</div>
                        <a href="#" class="button button-google"
                            onClick={() => handlerCount}
                        >creer compte</a>
                    </div>
                </form>

                {/* register form*/}

                <form action="" style={{ display: hide ? 'none' : 'inline' }} className="form">
                    <h1 class="form-title">
                        ENREGISTREZ-VOUS
                    </h1>
                    <div class="form-fields">
                        <div class="form-field">
                            <input type="text" placeholder="Name" required pattern="[a-zA-Z]+" title="Name can only contain letters." />
                        </div>

                        <div class="form-field">
                            <input type="email" placeholder="Email" required />
                        </div>
                        <div class="form-field">
                            <input type="password" placeholder="Password" required minlength="8" maxlength="128" />
                        </div>
                    </div>
                    <div class="form-buttons">
                        <button class="button">ENREGISTRER</button>
                        <div class="divider">ou</div>
                        <a href="#" class="button button-google"
                            onClick={() => { handlerCount() }}
                        >connection</a>
                    </div>
                </form>
            </div>
        </main>

    </>;
}

export default Login;
