// Filename - component/home.js

import { Link } from 'react-router-dom';

import './chat.css';
import axios from 'axios'
import { redirect,useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";

// import './styles.css';
function Home() {
    const [data, setData] = useState('');
    const [Modals, setModals] = useState(null);
    const [closes, setCloses] = useState(null);
    const [box, setBox] = useState(null);
    const [entete, setEntete] = useState('hide_b');
    const [isLoggedIn, setisLoggedIn]=useState(false);
    const navigate = useNavigate();

    useEffect(()=>{
        const loggedInUser=localStorage.getItem('Loggin');

        if( !loggedInUser){
            navigate('/login')
        }else{
            setisLoggedIn(true);
        }
      axios.get("http://localhost:5000/api/me")
            .then((response) => {
                const fetchd = response.data
                console.log("meesages " + fetchd)
            })
            .catch((error) => {
                console.log("meesages " + error)
            })
    });
    const [display, setDisplay] = useState('hide_modal')
    const handlerClose = () => {
        closes == null ? setCloses('show-modal') : setCloses(null);
    }
    const handlerChat = () => {
        display == 'hide_modal' ? setDisplay('show_modal') : setDisplay('hide_modal');

    }
    const handlerModal = () => {
        Modals == null ? setModals('show-modal') : setModals(null);
    }
   function handlerbox(){
    box==null? setBox('chats_hide'):setBox(null);
    entete==null? setEntete('hide_b'):setEntete(null);
   }

    return <>
        <nav>

            <label for="name" id="logo-text">
                <a href="#">ENA DRC</a>
            </label>

            <input type="checkbox" id="check" />
            <label for="check" ClassNameName="toggle">
                <i ClassNameName="fa-solid fa-bars-staggered"></i>
            </label>

            <ul>
                <li><a href="#" ClassNameName="active">Home</a></li>
                <li><a href="#">Features</a></li>

                <li><a href="#">Contact US</a></li>
                <li><a href="#">About</a></li>
            </ul>

            <div id="account-container">
                {
                    isLoggedIn?
                    <>
                        <a href="#">Logout</a>
                    </>
                    :
                   <>
                     <a href="#">Register</a>
                     <Link to='/login'>Login</Link>
                   </>
                    
                }
                
                
            </div>
        </nav>

        <header>
            <div id="header-container">
                <div id="header-page-title">
                    <h1>PLATEFORME DE  <br /> RECRUTEMENT</h1>
                    <p>la parteforme vous accompagne durant touts les precessus de Recrutement
                        <br /> Vous Pourrez en apprendre plus en soumettant une requette
                    </p>
                    <div id="header-subscribe-form">
                        <input type="email" name="" id="" placeholder="Enter your email" />
                        <input type="submit" value="Subscribe" />
                    </div>
                </div>
                <div id="header-page-image">
                    <img src={require('./image/Recrutement-en-Afrique.jpg')} alt="" />
                </div>
            </div>
        </header>

       

      



      

      

        {/* chat */}
        <div className='chatbox' onClick={()=>{handlerbox()}}>
            <p>chatbox</p>
        </div>
        <div className={'chats '+box}>
            <div className={'heads '+entete}>
                <div >
                        <img src={require('./image/xkuibmnlECWGlJSFK58HHvf4eIbulbEmbboinK9F.jpg')} alt="" className='profile'/>

                    Assistant Intelligent  <span></span>
                </div>
            </div>
            <div className="santaSays">
                <div class="img-box">
                    {/* <img src="https://img.freepik.com/free-vector/funny-santa-claus-celebrating-christmas_74855-962.jpg" alt="santaimg" /> */}
                </div>
                <div class="text-box-santa">
                    <div class="text">
                        <p>Hi there, my child!</p>
                        {/* <p>What can I help you with?</p> */}
                    </div>
                </div>

                <div class="userSays">
                    <div class="text">
                        <p>Hello, Santa!</p>
                        {/* <p>I'd like to know when you'll bring my gift?</p> */}
                    </div>
                </div>
                <div class="text-box-santa">
                    <div class="text">
                        {/* <p>Hi there, my child!</p> */}
                        <p>What can I help you with?</p>
                    </div>
                </div>
                <div class="userSays">
                    <div class="text">
                        {/* <p>Hello, Santa!</p> */}
                        <p>I'd like to know when you'll bring my gift?</p>
                    </div>
                </div>
            </div>
            <hr/>
            <div className="message-box">
            <div className={"message-input "+entete}>
                <input id="inputText" type="text" placeholder="What can I help you with?"/>
            </div>
           
        </div>
        </div>

    </>;
}

export default Home;
