// Filename - component/home.js

import { Link } from 'react-router-dom';

import './chat.css';
import axios from 'axios'
import { redirect, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import { AiOutlineSend } from "react-icons/ai";
// import './styles.css';
function Home() {
    const [data, setData] = useState([]);
    const [Modals, setModals] = useState(null);
    const [closes, setCloses] = useState(null);
    const [box, setBox] = useState(null);
    const [message, setMessage] = useState(null);
    const [user, setUser] = useState(null);
    const [entete, setEntete] = useState('hide_b');
    const [isLoggedIn, setisLoggedIn] = useState(false);
    const navigate = useNavigate();
    function getUserFunction() {
        let bearer = 'Bearer ' + (localStorage.getItem('Loggin'));
        axios.get("http://localhost:5000/api/me",
            {

                headers: { "Authorization": bearer }
            }

        )
            .then((response) => {
                const fetchd = response.data
                setUser(fetchd)
                console.log(fetchd.username)
            })
            .catch((error) => {
                console.log("meesages " + error)
            })
    }
    function getMessageFunction() {
        let bearer = 'Bearer ' + (localStorage.getItem('Loggin'));
        axios.get("http://localhost:5000/api/getMessage",
            {

                headers: { "Authorization": bearer }
            }

        )
            .then((response) => {
                const fetchd = response.data
                setData(fetchd.messages)



            })
            .catch((error) => {
                console.log("meesages " + error)
            })
        console.log(data)

    }
    useEffect(() => {
        const loggedInUser = localStorage.getItem('Loggin');
        const loggedInUserInfo = localStorage.getItem('user');


        if (!loggedInUser) {
            navigate('/login')
        } else {
            setisLoggedIn(true);
        };
        getUserFunction();
        getMessageFunction();
    }, []);
    const [display, setDisplay] = useState('hide_modal')
    const handlerClose = () => {
        closes == null ? setCloses('show-modal') : setCloses(null);
    }
    const handlerChat = () => {
        display == 'hide_modal' ? setDisplay('show_modal') : setDisplay('hide_modal');

    }
    const handlerModal = () => {
        Modals == null ? setModals('show-modal') : setModals(null);
    }
    const handleLogout = () => {
        localStorage.removeItem('Loggin');
        setisLoggedIn(false)

    }
    const handleSend = async e => {
        e.preventDefault();
        let bearer = 'Bearer ' + (localStorage.getItem('Loggin'));

        const user = { message, token: bearer };
        await axios.post(
            "http://localhost:5000/api/send",
            user
        )
            .then((response) => {
                const fetchd = response.data
                setMessage("")
                console.log(fetchd)
            })
            .catch((error) => {
                console.log("meesages " + error)
            })


    }
    function handlerbox() {
        box == null ? setBox('chats_hide') : setBox(null);
        entete == null ? setEntete('hide_b') : setEntete(null);
    }

    const datas = data.map((element, index) => {
        return (
            <>
                <div class="text-box-santa">
                    <div class="text">
                        <p>Hi there, my child!</p>
                    </div>
                </div>

                <div class="userSays">
                    <div class="text">
                        <p>Hello, Santa!</p>
                    </div>
                </div>
            </>
        );
    });
    return <>
        <nav>

            <label for="name" id="logo-text">
                <a href="#">ENA DRC</a>
            </label>

            <input type="checkbox" id="check" />
            <label for="check" ClassNameName="toggle">
                <i ClassNameName="fa-solid fa-bars-staggered"></i>
            </label>

            <ul>
                <li><a href="#" ClassNameName="active">Home</a></li>
                <li><a href="#">Features</a></li>

                <li><a href="#">Contact US</a></li>
                <li><a href="#">About</a></li>
            </ul>

            <div id="account-container">
                {
                    isLoggedIn ?
                        <>
                            <a href="#" onClick={handleLogout}>Logout</a>
                        </>
                        :
                        <>
                            <a href="#">Register</a>
                            <Link to='/login'>Login</Link>
                        </>

                }


            </div>
        </nav>

        <header>
            <div id="header-container">
                <div id="header-page-title">
                    <h1>PLATEFORME DE  <br /> RECRUTEMENT</h1>
                    <p>la parteforme vous accompagne durant touts les precessus de Recrutement
                        <br /> Vous Pourrez en apprendre plus en soumettant une requette
                    </p>
                    <div id="header-subscribe-form">
                        <input type="email" name="" id="" placeholder="Enter your email" />
                        <input type="submit" value="Subscribe" />
                    </div>
                </div>
                <div id="header-page-image">
                    <img src={require('./image/Recrutement-en-Afrique.jpg')} alt="" />
                </div>
            </div>
        </header>











        {/* chat */}
        <div className='chatbox' onClick={() => { handlerbox() }}>
            <p>chatbox</p>
        </div>
        <div className={'chats ' + box}>
            <div className={'heads ' + entete}>
                <div >
                    <img src={"https://img.freepik.com/free-vector/funny-santa-claus-celebrating-christmas_74855-962.jpg"} alt="" className='profile' />

                    {user ? user.username : " "}  <span></span>
                </div>
            </div>
            <div className="santaSays">
               

                {
                    isLoggedIn ?

                        datas :
                        <>
                            <h3>
                                please Loggin first here <Link to='/login'>Login</Link>
                            </h3>
                        </>
                }
            </div>

        <hr />
        <div className="message-box">
            <div className={"message-input " + entete}>
                <input id="inputText" type="text" value={message} onChange={(e) => setMessage(e.target.value)} placeholder="What can I help you with?" />
                <AiOutlineSend onClick={handleSend} />
            </div>

        </div>
    </div >

    </>;
}

export default Home;
